import ActionTypes from './actionTypes';

export const initialState = {
  connection: false,
};

export default function reducer(state = initialState, action = {}) {
  switch (action.type) {
    case ActionTypes.ON_LINE:
      return {
        ...state,
        connection: true,
      };
    case ActionTypes.OFF_LINE:
      return {
        ...state,
        connection: false,
      };
    default:
      return state;
  }
}
